import React, { useContext, useRef, useState } from "react";
import "../LoginPage/Login.css";

export default function Register() {
	const inputs = useRef([]);
	const addinputs = (e) => {
		if (e && !inputs.current.includes(e)) {
			inputs.current.push(e);
		}
	};

	const handleForm = (e) => {
		e.preventDefault();
	};

	return (
		<div className="main-container">
			<h1 className="main-title">TODO</h1>
			<form action="#" className="login-form">
				<label htmlFor="username">Username :</label>
				<input type="text" id="username" name="username" ref={addinputs} />
				<label htmlFor="pwd">Password :</label>
				<input type="password" name="pwd" id="pwd" ref={addinputs} />
				<label htmlFor="confirm-pwd">Confirm Password :</label>
				<input
					type="password"
					name="confirm-pwd"
					id="confirm-pwd"
					ref={addinputs}
				/>
				<span>
					Have an account ? <a href="/register">Login here</a>
				</span>
				<button type="submit" className="login-btn">
					Login
				</button>
			</form>
		</div>
	);
}
